    public void getCapital() {
        try
        {
            capital = Integer.parseInt(jTextField1.getText());
            time = Integer.parseInt(jTextField2.getText());
            interest = Integer.parseInt(jTextField3.getText());
        }

        catch (Exception e)
        {
            jLabel5.setText("PONER LOS DATOS CORRECTAMENTE");
        }
    }

    public double calulateCapital()
    {
        double rate = interest / 100.0;
        double result = capital * Math.pow(1 + rate, time);
        finalCapital = Math.round(result * 100.0) / 100.0;
        return finalCapital;
    }
    
    public void showFinalCapital()

    {
        jLabel5.setText("El capital final es: " + finalCapital + "$");
    }
    

